import React, { Component } from 'react';
import './App.css';
import Prs from './Person/Person';

class App extends Component {

  state ={
    persons: [
      {name:'Prakash',age:20},
      {name:'Prakash1',age:21},
      {name:'Prakash2',age:22},
    ],
    otherName: 'Testing2'
  }

  switchNameHandler = (newName) =>{
   // console.log('Test');
   this.setState({
    persons: [
      {name:newName,age:20},
      {name:'Prakash1',age:21},
      {name:'Prakash2',age:876},
    ]
   })

  } 

  switchInputHandler = (event) => {
    this.setState({
      persons: [
        {name:event.target.value,age:20},
        {name:'Prakash1',age:21},
        {name:'Prakash2',age:876},
      ]
     })

  }


  render() {
    return (
      <div className="App">
       <h1>Hello</h1>
       <button onClick={this.switchNameHandler.bind(this,'test1')}>Switch Name </button>
       <Prs name={this.state.persons[0].name} age={this.state.persons[0].age} click={this.switchNameHandler.bind(this,'test2')} changed={this.switchInputHandler} />
       <Prs name={this.state.persons[1].name} age={this.state.persons[1].age}>How are you?</Prs>
       <Prs name={this.state.persons[2].name} age={this.state.persons[2].age} /> 
       <h2>Test : {this.state.otherName}</h2>

      </div>
    );

  }
}

export default App;
