import React, { Component } from 'react';
import './App.css';
import Prs from './Person/Person';

class App extends Component {

  state ={
    persons: [
      {name:'Prakash',age:20},
      {name:'Prakash1',age:21},
      {name:'Prakash2',age:22},
    ],
    otherName: 'Testing2',
    showPerson: false
  }

  switchNameHandler = (newName) =>{
   // console.log('Test');
   this.setState({
    persons: [
      {name:newName,age:20},
      {name:'Prakash1',age:21},
      {name:'Prakash2',age:876},
    ]
   })

  } 

  switchInputHandler = (event) => {
    this.setState({
      persons: [
        {name:event.target.value,age:20},
        {name:'Prakash1',age:21},
        {name:'Prakash2',age:876},
      ]
     })
  }

  switchToggle =() =>{
    this.setState({showPerson : !this.state.showPerson})
  }


  render() {

    let persons = null;

    if(this.state.showPerson){
      persons = (
        <div>
           {this.state.persons.map( person => {
             return <Prs name = {person.name} age = {person.age}/> 
           })}
        </div>
      )
    }


    return (
      <div className="App">
       <h1>Hello</h1>
       <button onClick={this.switchToggle}>Switch Name </button>
        {persons}
       <h2>Test : {this.state.otherName}</h2>
      </div>
    );

  }
}

export default App;
