import React, { Component } from 'react';
import styles from './App.module.css';
// import Radium, { StyleRoot} from 'radium';
import Persons from '../components/Persons/Persons';
import Cockpit from '../components/Cockpit/Cockpit';

class App extends Component {

  constructor(props){
    super(props);
    console.log('[App.js] Inside Constructor',props);
  }

  componentWillMount(){
    console.log('[App.js] Inside componentWillMount',props);
  }

  componentDidMount(){
    console.log('[App.js] Inside componentDidMount',props);
  }

  state ={
    persons: [
      {id:'fewdf',name:'Prakash',age:20},
      {id:'ccxcd',name:'Prakash1',age:21},
      {id:'ewrqwe',name:'Prakash2',age:22},
    ],
    otherName: 'Testing2',
    showPersons: false
  }

  switchNameHandler = (event,id) =>{
   // console.log('Test');
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });

    const person = { ...this.state.persons[personIndex]};

    person.name = event.target.value;

    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({
      persons: persons
    // persons: [
    //   {name:newName,age:20},
    //   {name:'Prakash1',age:21},
    //   {name:'Prakash2',age:876},
    // ]
   })

  } 

  // switchInputHandler = (event) => {
  //   this.setState({
  //     persons: [
  //       {name:event.target.value,age:20},
  //       {name:'Prakash1',age:21},
  //       {name:'Prakash2',age:876},
  //     ]
  //    })
  // }

  switchToggle =() =>{
    this.setState({showPersons : !this.state.showPersons})

  }

  deletePersonHandler = (indexPerson) => {
    //const persons = this.state.persons.slice();
    const persons = [...this.state.persons];
    persons.splice(indexPerson,1);
    this.setState({persons:persons});
  }


  render() {
    console.log('[App.js] Inside render',props);
    let persons = null;
    // const style ={
    //   backgroundColor:'green',
    //   color:'white',
    //   font:'inherit',
    //   border: '1px solid blue',
    //   padding: '8px',
    //   // cursor: 'pointer',
    //   // ':hover':{
    //   //   backgroundColor:'lightgreen',
    //   //   color:'black',
    //   // }
    // }

    if(this.state.showPersons){
      persons = <Persons 
          persons = {this.state.persons}
          clicked={this.deletePersonHandler}
          changed={this.switchNameHandler} />

      //style.backgroundColor='red';
      // style[':hover'] = {
      //   backgroundColor:'salmon',
      //   color:'black'
      // }
    }

    return (
      // <StyleRoot>    
      <div className={styles.App}>
        <Cockpit 
            title = {this.props.title}
            showPersons ={this.state.showPersons}
            persons = {this.state.persons}
            clicked = {this.switchToggle}
            />
        {persons}
       <h2>Test : {this.state.otherName}</h2>
      </div>
      // </StyleRoot>
    );

  }
}

export default App;
