import React ,{Component} from 'react';

class FullPost extends Component {
    render() {
        let post = <p style={{textAlign:'center'}}>Please select a post</p>
        if(this.props.id){
            post = (
                <div>
                    <h1>Title</h1>
                    <p>Content</p>
                    <div>
                        <button>Delete</button>
                    </div>
                </div>
            );
        }
        return post;
    }
}

export default FullPost;
